const items = $input.all();
if (items.length === 0) {
  return [{ json: { error: "No input data received" } }];
}

const serpData = items[0].json;
const keyword = $("Input Keyword").item.json.keyword;

const output = {
  keyword: keyword,
  serpFeatures: {
    organicResults: [],
    featuredSnippet: null,
    peopleAlsoAsk: [],
    relatedSearches: [],
    aiOverview: null,
    topStories: [],
    richSnippets: []
  },
  metadata: {
    totalResults: serpData.search_information?.total_results || 0,
    searchTime: serpData.search_information?.time_taken_displayed || "0 sec"
  },
  topUrls: []
};

if (Array.isArray(serpData.organic_results)) {
  output.serpFeatures.organicResults = serpData.organic_results
    .slice(0, 10)
    .map((result, index) => ({
      position: index + 1,
      title: result.title || "",
      link: result.link || "",
      displayedLink: result.displayed_link || result.link || "",
      snippet: result.snippet || "",
      richSnippet: result.rich_snippet || null,
      sitelinks: result.sitelinks || null
    }));

  output.topUrls = output.serpFeatures.organicResults
    .slice(0, 5)
    .map(r => r.link)
    .filter(link => link && typeof link === "string" && link.startsWith("http"));
}

if (serpData.answer_box) {
  output.serpFeatures.featuredSnippet = {
    type: serpData.answer_box.type || "unknown",
    title: serpData.answer_box.title || "",
    snippet: serpData.answer_box.snippet || serpData.answer_box.answer || "",
    link: serpData.answer_box.link || "",
    list: serpData.answer_box.list || null
  };
}

if (Array.isArray(serpData.related_questions)) {
  output.serpFeatures.peopleAlsoAsk = serpData.related_questions.map(q => ({
    question: q.question || "",
    snippet: q.snippet || "",
    title: q.title || "",
    link: q.link || ""
  }));
}

if (Array.isArray(serpData.related_searches)) {
  output.serpFeatures.relatedSearches = serpData.related_searches
    .map(s => s.query || s)
    .filter(Boolean);
}

if (serpData.ai_overview) {
  output.serpFeatures.aiOverview = {
    text: serpData.ai_overview.text || serpData.ai_overview.answer || "",
    sources: Array.isArray(serpData.ai_overview.sources) ? serpData.ai_overview.sources : []
  };
}

if (Array.isArray(serpData.top_stories)) {
  output.serpFeatures.topStories = serpData.top_stories.map(story => ({
    title: story.title || "",
    link: story.link || "",
    source: story.source || "",
    date: story.date || ""
  }));
}

output.serpFeatures.richSnippets = output.serpFeatures.organicResults
  .filter(r => r.richSnippet)
  .map(r => ({
    url: r.link,
    type: r.richSnippet.type || "unknown",
    data: r.richSnippet
  }));

return [{ json: output }];

const item = $input.item.json;
const url = $('Split URLs').item.json.topUrls;

const h1 = Array.isArray(item.h1) ? item.h1 : (item.h1 ? [item.h1] : []);
const h2 = Array.isArray(item.h2) ? item.h2 : (item.h2 ? [item.h2] : []);
const h3 = Array.isArray(item.h3) ? item.h3 : (item.h3 ? [item.h3] : []);

const headingStructure = {
  h1: h1,
  h2: h2,
  h3: h3
};

const markdown = item.markdown || '';
const wordCount = markdown.split(/\s+/).filter(w => w.length > 0).length;

const stopWords = new Set([
  'the', 'be', 'to', 'of', 'and', 'a', 'in', 'that', 'have', 'i',
  'it', 'for', 'not', 'on', 'with', 'he', 'as', 'you', 'do', 'at',
  'this', 'but', 'his', 'by', 'from', 'they', 'we', 'say', 'her', 'she',
  'or', 'an', 'will', 'my', 'one', 'all', 'would', 'there', 'their',
  'what', 'so', 'up', 'out', 'if', 'about', 'who', 'get', 'which',
  'go', 'me', 'when', 'make', 'can', 'like', 'time', 'no', 'just',
  'him', 'know', 'take', 'people', 'into', 'year', 'your', 'good',
  'some', 'could', 'them', 'see', 'other', 'than', 'then', 'now',
  'look', 'only', 'come', 'its', 'over', 'think', 'also', 'back',
  'after', 'use', 'two', 'how', 'our', 'work', 'first', 'well',
  'way', 'even', 'new', 'want', 'because', 'any', 'these', 'give',
  'day', 'most', 'us', 'is', 'was', 'are', 'been', 'has', 'had',
  'were', 'said', 'did', 'having', 'may', 'should', 'am', 'being'
]);

const words = markdown.toLowerCase().match(/\b[a-z]{4,}\b/g) || [];
const filteredWords = words.filter(word => !stopWords.has(word));

const wordFreq = {};
filteredWords.forEach(word => {
  wordFreq[word] = (wordFreq[word] || 0) + 1;
});

const topKeywords = Object.entries(wordFreq)
  .filter(([word, freq]) => freq >= 3)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 15)
  .map(([word, freq]) => ({ word, frequency: freq }));

const contentPreview = markdown.substring(0, 800);

return {
  url: url,
  title: item.title || 'No title found',
  metaDescription: item.metaDescription || '',
  metaKeywords: item.metaKeywords || '',
  headingStructure: headingStructure,
  wordCount: wordCount,
  topKeywords: topKeywords,
  contentPreview: contentPreview
};

