      "context": "2-3 sentences of background",
      "thesis": "Main point/promise of the article",
      "estimatedWordCount": 150
    },
    "sections": [
      {
        "heading": "H2 Heading (descriptive, keyword-relevant)",
        "purpose": "What this section accomplishes",
        "keyPoints": ["Point 1", "Point 2", "Point 3"],
        "semanticKeywords": ["related terms to include naturally"],
        "estimatedWordCount": 400,
        "subsections": [
          {
            "heading": "H3 Subheading",
            "keyPoints": ["Specific point"],
            "estimatedWordCount": 200
          }
        ]
      }
    ],
    "conclusion": {
      "summary": "Key takeaways recap",
      "cta": "Call to action",
      "estimatedWordCount": 150
    },
    "faqSection": [
      {
        "question": "Question from PAA or user intent",
        "answerBrief": "1-2 sentence answer outline"
      }
    ],
    "targetMetrics": {
